PRAGMA foreign_keys = ON;CREATE TABLE IF NOT EXISTS raw_api_responses (    id INTEGER PRIMARY KEY AUTOINCREMENT,    fetched_at TEXT NOT NULL,    source TEXT NOT NULL,    endpoint TEXT NOT NULL,    payload_json TEXT NOT NULL);CREATE INDEX IF NOT EXISTS idx_raw_api_responses_fetched_atON raw_api_responses(fetched_at);CREATE INDEX IF NOT EXISTS idx_raw_api_responses_source_endpointON raw_api_responses(source, endpoint);CREATE TABLE IF NOT EXISTS products (    product_id INTEGER PRIMARY KEY,    title TEXT NOT NULL,    brand TEXT,    category TEXT,    last_seen_at TEXT NOT NULL);CREATE INDEX IF NOT EXISTS idx_products_last_seen_atON products(last_seen_at);CREATE TABLE IF NOT EXISTS prices_history (    id INTEGER PRIMARY KEY AUTOINCREMENT,    product_id INTEGER NOT NULL,    fetched_at TEXT NOT NULL,    price REAL NOT NULL,    currency TEXT NOT NULL,    FOREIGN KEY(product_id) REFERENCES products(product_id));CREATE INDEX IF NOT EXISTS idx_prices_history_product_id_fetched_atON prices_history(product_id, fetched_at);CREATE TABLE IF NOT EXISTS chat_settings (    chat_id INTEGER PRIMARY KEY,    default_threshold_percent REAL NOT NULL,    updated_at TEXT NOT NULL);CREATE TABLE IF NOT EXISTS subscriptions (    chat_id INTEGER NOT NULL,    product_id INTEGER NOT NULL,    threshold_percent REAL NOT NULL,    is_active INTEGER NOT NULL DEFAULT 1,    created_at TEXT NOT NULL,    PRIMARY KEY (chat_id, product_id));CREATE INDEX IF NOT EXISTS idx_subscriptions_chat_activeON subscriptions(chat_id, is_active);CREATE INDEX IF NOT EXISTS idx_subscriptions_activeON subscriptions(is_active);